choose.when:
select tt.*, x.qxdm sid from dic_xzq x left join (select * from (select ycdm, t.qxdm sid, count(*) value from bdcdj_check t where 1 = 1
and t.sjrksj between to_date(#{starttime}, 'YYYY-MM-DD') and to_date(#{endtime}, 'YYYY-MM-DD')
group by ycdm, qxdm) pivot(sum(value) for ycdm in(201, 202, 203, 204, 205))) tt on x.qxdm = tt.sid where x.pqxdm=#{cityCode}

mapper.select:
select * from ZDJBXX_ATT  where ywbsid=#{ywbsid}
 union all
 select * from ZDJBXX_ATT_HIS  where ywbsid=#{ywbsid}
